
string Input = "";
//string Input = "mjqjpqmgbljsphdztnvjfqwrcgsmlb";
int messageLength = 14;
for (int posCount = 0; posCount < Input.Length; posCount++)
{
    if(posCount > messageLength)
    {
        bool found = true;
        string checkString = Input.Substring(posCount - messageLength + 1, messageLength);
        for (int checkCount = 0; checkCount < messageLength; checkCount++)
        {
            if (checkString.Count(i => i == Input[posCount - checkCount]) > 1) 
            {
                found = false;
            }
        }
        if (found)
        {
            Console.WriteLine(posCount + 1);
            posCount = Input.Length;
        }
        
    }
}